import React from 'react';
import Paper from '@material-ui/core/Paper';
import { makeStyles, createStyles, Theme } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Avatar from '@material-ui/core/Avatar';
import Typography from '@material-ui/core/Typography';
import { Button } from '@material-ui/core';
import { Link } from 'react-router-dom';
import { createMuiTheme, ThemeProvider } from '@material-ui/core/styles';
import Appbar from '../../../shared/Appbar';

const theme = createMuiTheme();

theme.typography.h3 = {
  fontSize: '1.2rem',
  '@media (min-width:600px)': {
    fontSize: '1.5rem',
  },
  [theme.breakpoints.up('md')]: {
    fontSize: '2rem',
  },
};


export default function BookDetail() {
  return (
    <div style={{color:'skyblue', padding : 100, paddingTop :150}}>
      <Appbar/>
        <Paper>
          <Grid container>
            <Typography variant='subtitle2'style={{marginTop:40, marginLeft :50}}>
              판매일자 2020.12.25
            </Typography>
          </Grid>
          <Grid container spacing={2}>
            <Grid item>
              <Paper elevation={0}>
                <img src='https://ifh.cc/g/2JYFaB.jpg' width='200px' height='250px' alt='?'/>
              </Paper>
            </Grid>
            <Grid item xs={12} sm container>
              <Grid item xs container direction="column" spacing={2}>
                <Grid item xs>
                <ThemeProvider theme={theme}>
                    <Typography variant="h3">문제해결을 위한 컴퓨팅 사고</Typography>
                  </ThemeProvider>
                </Grid>
              </Grid>
            </Grid>
          </Grid>
        </Paper>
    </div>
  )
}
