import React from 'react';
// import useAxios from 'axios-hooks';
import {
  TextField, Grid, Typography, InputBase, makeStyles, fade, Paper, Button,
} from '@material-ui/core';
import Checkboxes from './Checkboxes';

const useStyles = makeStyles((theme) => ({
  paper: {
    backgroundColor: '#ffffff',

    display: 'flex',
    flexWrap: 'wrap',
    borderRadius: '40px',
    '& > *': {
      margin: theme.spacing(40),
      width: theme.spacing(50),
      height: theme.spacing(10),
    },
  },
  font: {
    fontSize: 30,

  },
  bluefont: {
    color: '#87abf5',
  },
  confirmButton: {
    backgroundColor: '#f5a1a1',
  },
  search: {
    position: 'relative',
    borderRadius: theme.shape.borderRadius,
    backgroundColor: fade(theme.palette.common.white, 0.15),
    '&:hover': {
      backgroundColor: fade(theme.palette.common.white, 0.25),
    },
    marginRight: theme.spacing(2),
    marginLeft: 0,
    width: '100%',
    [theme.breakpoints.up('sm')]: {
      marginLeft: theme.spacing(3),
      width: 'auto',
    },
  },
  searchIcon: {
    padding: theme.spacing(0, 2),
    height: '100%',
    position: 'absolute',
    pointerEvents: 'none',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  inputRoot: {
    color: 'inherit',
  },
  inputInput: {
    padding: theme.spacing(1, 1, 1, 0),
    // vertical padding + font size from searchIcon
    paddingLeft: `calc(1em + ${theme.spacing(4)}px)`,
    transition: theme.transitions.create('width'),
    width: '100%',
    [theme.breakpoints.up('md')]: {
      width: '20ch',
    },
  },
}));
export default function SharedPage(): JSX.Element {
  // const [{ data }, reload] = useAxios(
  //   { url: 'http://localhost:3000/room', method: 'POST' },
  // );
  const classes = useStyles();
  return (
    <Paper style={{ width: '80%', height: '100vh', borderRadius: '40px' }} elevation={0}>
      <Grid container style={{ padding: 80 }}>
        <Grid item xs={12} style={{ paddingBottom: 30, position: 'relative' }}>
          <Typography style={{ fontSize: 30 }}>
            판매 하기
          </Typography>
        </Grid>

        <Grid item xs={1} style={{ paddingBottom: 30, justifyContent: 'left' }}>
          <Typography style={{ fontSize: 20 }}>
            판매할 책
          </Typography>
        </Grid>

        <Grid item xs={11} style={{ paddingBottom: 30, justifyContent: 'left' }}>
          <div className={classes.search}>
            <InputBase
              placeholder="ISBN 또는 책이름 검색..."
              classes={{
                root: classes.inputRoot,
                input: classes.inputInput,
              }}
              inputProps={{ 'aria-label': 'search' }}
            />
            <img src="/i_search@3x.png" alt="search" style={{ width: 30, height: 30 }} />
          </div>
        </Grid>

        <Grid item xs={1} style={{ paddingBottom: 10, justifyContent: 'left' }}>
          <Typography variant="h6" className={classes.bluefont}>
            책상태
          </Typography>
        </Grid>

        <Checkboxes title="책 내부" />
        <Checkboxes title="책 외부" />
        <Checkboxes title="거래방법" />

        <Grid item xs={1} style={{ paddingTop: 30, paddingBottom: 10, justifyContent: 'left' }}>
          <Typography variant="h6" className={classes.bluefont}>
            추가설명
          </Typography>
        </Grid>

        <Grid item xs={12} style={{ paddingLeft: 20, paddingBottom: 10 }}>
          <TextField
            id="outlined-multiline-static"
            style={{ width: '100%' }}
            multiline
            rows={4}
            defaultValue="추가설명을 적어주세요."
            variant="outlined"
            fullWidth
          />
          {/* <input id="outlined" style={{ width: '100%' }} /> */}
        </Grid>

        <Grid container style={{ paddingTop: 30, paddingBottom: 5 }}>
          <Grid item xs={2}>
            <Typography variant="h6" className={classes.bluefont}>
              판매할 가격
            </Typography>
          </Grid>
          <Grid item xs={2}>
            <TextField
              id="standard-number"
            />
          </Grid>
          <Grid item xs={2}>
            <Typography variant="h6" className={classes.bluefont}>
              원
            </Typography>
          </Grid>
        </Grid>
      </Grid>

      <Grid item xs={12} style={{ paddingBottom: 3 }}>
        <Typography style={{ fontSize: 15 }}>
          거래시 발생하는 개인정보 열람(연락처 공개)에 동의하십니까?
        </Typography>
      </Grid>

      <Grid item style={{ paddingLeft: 80, paddingBottom: 10, justifyContent: 'center' }}>
        <Button className={classes.confirmButton} variant="contained">
          판매하기
        </Button>
      </Grid>

    </Paper>
  );
}
