import React from 'react';
import Paper from '@material-ui/core/Paper';
import { makeStyles, createStyles, Theme } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Avatar from '@material-ui/core/Avatar';
import Typography from '@material-ui/core/Typography';
import { Button, responsiveFontSizes } from '@material-ui/core';
import { Link } from 'react-router-dom';
import { createMuiTheme, ThemeProvider } from '@material-ui/core/styles';
import Appbar from '../../../shared/Appbar';


const theme = createMuiTheme();

theme.typography.h3 = {
  fontSize: '30px',
  '@media (min-width:600px)': {
    fontSize: '33px',
  },
  [theme.breakpoints.up('md')]: {
    fontSize: '30px',
  },
};
const useStyles = makeStyles((theme) => ({
  textprimary:{
    color:'#a0a0a0',
    responsiveFontSizes:'15px'
  },
  pricetext:{
    color:'#ffc0c0c0',
    fontSize:'24px',
  }
}));

export default function BookDetail() {
  const classes = useStyles();
  return (
    <div style={{background:'primary', padding : 100, paddingTop :150}}>
      <Appbar/>
        <Paper>
          <Grid container>
            <Typography variant='subtitle2'style={{marginTop:40, marginLeft :55}} className={classes.textprimary}>
              판매일자 2020.12.25
            </Typography>
          </Grid>
          <Grid container spacing={2} style={{marginLeft:50, marginTop:1}}>
            <Grid item>
              <Paper elevation={0}>
                <img src='https://ifh.cc/g/2JYFaB.jpg' width='200px' height='250px' alt='?'/>
              </Paper>
            </Grid>
            <Grid item xs={12} sm container>
              <Grid item xs container direction="column" spacing={2}>
                <Grid item xs>
                  <ThemeProvider theme={theme}>
                    <Typography variant="h3">문제해결을 위한 컴퓨팅 사고(이공 계열 전공자를 위한)</Typography>
                  </ThemeProvider>
                  <Typography className={classes.textprimary} style={{marginTop:10}}>
                    저자 writer
                  </Typography>
                  <Typography className={classes.textprimary}>
                    출판사 publisher
                  </Typography>
                  <Typography className={classes.textprimary}>
                    거래방법 <strong>직거래</strong> 택배거래
                  </Typography>
                </Grid>
                <Grid container >
                  <Grid item xs={1}>
                    <Typography className={classes.pricetext}>
                      <strong>19000</strong>
                    </Typography>
                  </Grid>
                  <Grid item alignContent='center'> 
                    <Typography className={classes.textprimary} gutterBottom>
                      21000
                    </Typography>
                  </Grid>
                </Grid>  
              </Grid>
            </Grid>
          </Grid>
          <Grid container spacing={2} style={{marginLeft:'50', marginTop:'1s0'}}>
            <Grid item>
              <Typography variant='h6'>
                책내용
              </Typography>
            </Grid> 
          </Grid>
        </Paper>
    </div>
  )
}
